import React from 'react';
import "bootstrap/dist/css/bootstrap.min.css";
import { Button, Form, FormGroup, Label, Input, FormText } from 'reactstrap';
import "./style.css";
import { FaWhatsapp,FaWhatsappSquare } from 'react-icons/fa';

// const WpOption1 = require("../assets/Wa1.png");
// const WpOption2 = require("../assets/wa2.jpg");
const Index = () => {
    return (
        <div className="all-main-container">
            <Form>
                <FormGroup tag="fieldset">
                  
                    <legend>Select WhatsApp Share button </legend>
                    <div style={{display:"flex"}}>
                    <FormGroup check>
                        <Label check>
                            <Input type="radio" name="radio1" value="option1" />{' '}
                            {/* <img height="15px" width="15px" src={"../assets/Wa1.png"} alt="ss" ></img> */}
                            {/* <i className="fa fa-whatsapp" aria-hidden="true"></i> */} <FaWhatsapp color="green" size="2em"/>
                        </Label>
                    </FormGroup>
                    <FormGroup check>
                        <Label check>
                            <Input type="radio" name="radio1" value="option2" />{' '}<FaWhatsappSquare color="green" size="2em"/>
                            {/* <i className="fa fa-whatsapp" aria-hidden="true"></i> */}
                            {/* <img height="15px" width="15px" src={"../assets/wa2.jpg"} alt="ss" ></img> */}
                        </Label>
                    </FormGroup>
                    </div>
                </FormGroup>
                <Button>Submit</Button>
            </Form>
        </div>
    );
}

export default Index;
